import React,{Component} from "react";
import "../../scss/components/__sendnotification.scss";







const checkboxes = [
    {
      name: 'check-box-1',
      key: 'checkBox1',
      label: 'Check Box 1',
    },
    {
      name: 'check-box-2',
      key: 'checkBox2',
      label: 'Check Box 2',
    },
    {
      name: 'check-box-3',
      key: 'checkBox3',
      label: 'Check Box 3',
    },
    {
      name: 'check-box-4',
      key: 'checkBox4',
      label: 'Check Box 4',
    },
  ];

class Send extends Component {


    constructor(props) {
        super(props);
    
        this.state = {
          showActionFilterList: false,
          checkedItems: new Map(),
        }
    
        this.handleChange = this.handleChange.bind(this);
      }
    render(){
        return(
          
                <div className="notification-wrapper">
                  <div className="Selectusers">
                     
                         <p>User</p>
                         <p>
                         Select All<input className="checkbox" type="checkbox" name="users" value="selectall"/> </p>
                     
                  </div>
                  <div>
                      <form>
                          
                          {/* <input type="checkbox"/>User A
                          <input type="checkbox"/>User B
                          <input type="checkbox"/>User C */}
                          <input type="checkbox" name="vehicle1" value="Bike"/> I have a bike<br></br>

                          checkboxes.map(item => (
            <label key={item.key}>
              {item.name}
              <Checkbox name={item.name} checked={this.state.checkedItems.get(item.name)} onChange={this.handleChange} />
            </label>
          ))

                          
                      </form>
                  </div>


                </div>
           
        )
    }

};

export default Send;
